<?php
function Card($titulo, $precio) {
    echo '<div class="relative h-full w-full border border-black rounded-lg p-5 hover:cursor-pointer">';
    echo '<div class="absolute inset-0 z-10 bg-gradient-to-r from-transparent to-black rounded-lg"></div>'; // Gradiente de fondo
    echo '<img class="absolute inset-0 h-full w-full object-cover z-0 rounded-lg" src="data:image/jpeg;base64," />';
    echo '<div class="relative z-20 text-white flex flex-col text-right text-2xl">';
    echo '<h2>' . $titulo . '</h2>';
    echo '<p>' . $precio . '</p>';
    echo '</div>';
    echo '</div>';
}
?>
